mapper.select:
SELECT boardid as boardId, CASE WHEN LENGTH(title) > 50 THEN CONCAT(LEFT(title, 50), '...') ELSE title END title, DATE_FORMAT(created, '%Y.%m.%d %H:%i') created, creator, published, hits FROM board
WHERE title LIKE CONCAT('%', #{searchWrd},'%')
ORDER BY board.created DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO board (
	title,
	contents,
	creator
) VALUES (
	#{title},
	#{contents},
	#{creator}
)

mapper.update:
UPDATE
	board
SET
	published = #{published}
WHERE
	boardid = #{boardId}

mapper.select:
SELECT boardid as boardId, CASE WHEN LENGTH(title) > 50 THEN CONCAT(LEFT(title, 50), '...') ELSE title END title, DATE_FORMAT(created, '%Y.%m.%d %H:%i') created, creator, hits, ( SELECT COUNT(boardid) as CNT FROM board WHERE published = 1
AND title LIKE CONCAT('%', #{searchWrd},'%')
) as totalPages, #{pageIndex} as pageIndex FROM board WHERE published = 1
AND title LIKE CONCAT('%', #{searchWrd},'%')
ORDER BY board.created DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
